import java.util.LinkedList;
import java.util.List;

public class homework1_5 {

    //returns the count of how often the passed pattern is in the passed string
    public static int patternFinder(String dna, String pattern){
        int counter = 0;

        for (int i = 0; i < dna.length() - pattern.length() + 1; i++){
            if (dna.startsWith(pattern, i)){
                counter++;
            }
        }
        return counter;
    }

    //main function
    public static void main(String[] args)
    {
        List<String> stringList = new LinkedList<>();
        String dna = "";

        int patternLength = 12;
        int L = 511;
        int timesPoppedUp = 20;

        for(int i = 0; i < dna.length() - L; i++){
            String window = dna.substring(i, i + L);
            for (int j = 0; j < window.length() - patternLength; j++){
                String pattern = window.substring(j, j + patternLength);
                int count = patternFinder(window, pattern);
                if (count == timesPoppedUp && !stringList.contains(pattern)){
                    stringList.add(pattern);
                }
            }
        }

        System.out.println(stringList);

    }
}
