import java.io.FileNotFoundException;

public class rosalind18 {

    public static int editDistance(String str1, String str2){
        int m = str1.length();
        int n = str2.length();

        int anotherMatrix[][] = new int[m + 1][n + 1];

        for (int i = 0; i <= m; i++){
            for (int j = 0; j <= n; j++){
                if ( i == 0){
                    anotherMatrix[i][j] = j;
                }

                else if (j == 0){
                    anotherMatrix[i][j] = i;
                }

                else if (str1.charAt(i - 1) == str2.charAt(j - 1)){
                    anotherMatrix[i][j] = anotherMatrix [i-1][j-1];
                }

                else {
                    anotherMatrix[i][j] = 1 + bestBet(anotherMatrix[i][j-1], anotherMatrix[i-1][j], anotherMatrix[i-1][j-1]);
                }
            }
        }

        return anotherMatrix[m][n];
    }

    static int bestBet(int column, int row, int diagonal){
        if (column <= row && column <= diagonal){
            return column;
        }
        else if (row <= column && row <= diagonal){
            return row;
        }
        else {
            return diagonal;
        }
    }

    public static void main(String[] args) throws FileNotFoundException{
        String string = "";
        String string2 = "";

        int ED = editDistance(string, string2);

        System.out.println(ED);
    }
}
